import React, { useContext, useState } from "react";
import { Upload, message } from "antd";
import UploadedImages from "../contexts/UploadedImages";
import useAxios from "../hooks/useAxios";
import useUrlBuilder from "../hooks/useUrlBuilder";
import { SearchBar } from "../components/SearchBar";
import { VideoList } from "../components/VideoList";
import { VideoDetail } from "../components/VideoDetail";
import youtube, { KEY } from "../api/youtube";
import FirebaseContext from "../firebase/context";
import { vision } from "../env";

const { Dragger } = Upload;

function FileUpload() {
  const uploaded = useContext(UploadedImages);
  const { loading, setUploadedImages } = uploaded;
  const [tesText, settesText] = useState("");

  const [videos, setVideos] = useState([]);
  const [selectedVideo, setSelectedVideo] = useState(null);
  const { firebase } = React.useContext(FirebaseContext);

  function handleSubmit() {
    const newLink = {
      videos,
      tesText
    };
    console.log("waa");
    //firebase.db.collection("links").add(newLink);
    //const voteRef = firebase.db.collection("category").doc(sub);
    /*    voteRef.get().then(doc => {
      if (doc.exists) {
         const prevNum = doc.data().numPosts;
        let newNum = prevNum + 1;
        console.log(doc.data().numPosts);
        firebase.db
          .collection("category")
          .doc(sub)
          .set(
            {
              numPosts: newNum
            },
            { merge: true }
          ); 
      }*/
  }

  const onSearchSubmit = async searchTerm => {
    const response = await youtube.get("/search", {
      params: {
        q: searchTerm,
        part: "snippet",
        maxResults: 3,
        key: KEY
      }
    });
    const responseArr = response.data.items;
    const firstVideo =
      responseArr[0].id.kind !== "youtube#channel"
        ? responseArr[0]
        : responseArr[1];
    setVideos(responseArr);
    setSelectedVideo(firstVideo);
    console.log(responseArr);
  };
  const [ocrState, ocrSend] = useAxios({
    url: `images:annotate`,
    method: "POST"
  });

  const endpoint = "https://vision.googleapis.com/v1/images:annotate";

  const preFormat = arr => {
    return [arr[0].x, arr[0].y, arr[1].x - arr[0].x, arr[3].y - arr[0].y];
  };

  const tidyResults = raw => {
    let results = {
      texts: [],
      boundings: []
    };
    raw.map((item, index) => {
      if (index > 0) {
        results.texts.push(item.description);
        results.boundings.push(preFormat(item.boundingPoly.vertices));
      }
    });
    return results;
  };

  const messageKey = "loadingMsg";

  const onLoading = () => {
    message.loading({
      content: "Analyzing image..",
      duration: 0,
      key: messageKey
    });
  };

  const props = {
    name: "file",
    multiple: false,
    accept: ".png,.jpeg,.jpg",
    showUploadList: false,
    disabled: loading,
    beforeUpload: file => {
      onLoading(true);
      // onLoading(false)

      setUploadedImages({
        loading: true
      });
      const reader = new FileReader();

      reader.onload = e => {
        const base64 = e.target.result.toString().replace(/^data:(.*,)?/, "");
        ocrSend(
          {
            url: endpoint,
            params: {
              key: vision.api_key
            },
            data: {
              requests: [
                {
                  image: {
                    content: base64
                  },
                  features: [
                    {
                      type: "DOCUMENT_TEXT_DETECTION"
                    }
                  ]
                }
              ]
            }
          },
          res => {
            console.log(res);
            console.log(res.responses[0].fullTextAnnotation.text);
            settesText(res.responses[0].fullTextAnnotation.text);
            const searchVal = res.responses[0].fullTextAnnotation.text.split(
              /\s*[\r\n]+\s*/g
            );
            console.log(searchVal);
            console.log(searchVal[7]);
            // console.log(onSearchSubmit(searchVal[7]));
            onSearchSubmit(searchVal[7]);
            setUploadedImages({
              loading: false,
              base64,
              ocrResultsRaw: res.responses[0],
              ocrResults: tidyResults(res.responses[0].textAnnotations),
              createdAt: new Date()
            });
            console.log(new Date());
            message.success({
              content: `action done successfully.`,
              key: messageKey
            });
          },
          e => {
            console.log(e);
            setUploadedImages({
              loading: false
            });
            message.error({
              content: `Ops, Something wrong`,
              key: messageKey
            });
          }
        );
      };
      reader.readAsDataURL(file);

      // Prevent upload
      return false;
    }
    // action: 'https://www.mocky.io/v2/5cc8019d300000980a055e76',
    // onChange(info) {
    //   const { status } = info.file;
    //   if (status !== 'uploading') {
    //     console.log(info.file, info.fileList);
    //   }
    //   if (status === 'done') {
    //     message.success(`${info.file.name} file uploaded successfully.`);
    //   } else if (status === 'error') {
    //     message.error(`${info.file.name} file upload failed.`);
    //   }
    // },
  };

  return (
    <>
      <Dragger {...props}>
        <p className="ant-upload-drag-icon">Inbox</p>
        <p className="ant-upload-text">
          Click or drag image to this area to start analyze
        </p>

        <p className="ant-upload-hint">Only accept jpeg,jpg and png files.</p>
      </Dragger>
      <div className="five wide column">
        {tesText}
        <VideoList videos={videos} setSelectedVideo={setSelectedVideo} />
      </div>
      <form
        onSubmit={handleSubmit}
        className="form-center subreddit-padding mtother"
      >
        <div className="form__margin">
          <button className="button cda-remove" type="submit">
            Submit
          </button>
        </div>
      </form>
    </>
  );
}

export default FileUpload;
