import React, { useState } from "react";
import "./App.css";
import youtube, { KEY } from "./api/youtube";
import { Layout, Menu, Breadcrumb } from "antd";
import FileUpload from "./components/FileUpload";
import ResultsTable from "./components/ResultsTable";
import { SearchBar } from "./components/SearchBar";
import { VideoDetail } from "./components/VideoDetail";
import { VideoList } from "./components/VideoList";
import Results from "./components/Results";
import UploadedImagesProvider from "./contexts/UploadedImagesProvider";
import firebase, { FirebaseContext } from "./firebase";

const { Header, Content, Footer } = Layout;

function App() {
  const [videos, setVideos] = useState([]);
  const [selectedVideo, setSelectedVideo] = useState(null);

  const onSearchSubmit = async searchTerm => {
    const response = await youtube.get("/search", {
      params: {
        q: searchTerm,
        part: "snippet",
        maxResults: 11,
        key: KEY
      }
    });
    const responseArr = response.data.items;
    const firstVideo =
      responseArr[0].id.kind !== "youtube#channel"
        ? responseArr[0]
        : responseArr[1];
    setVideos(responseArr);
    setSelectedVideo(firstVideo);
  };
  return (
    <>
      <FirebaseContext.Provider value={{ firebase }}>
        <div className="ui container">
          <SearchBar onSearchSubmit={onSearchSubmit} />
          <div className="ui grid">
            <div className="ui row">
              <div className="eleven wide column">
                <VideoDetail video={selectedVideo}></VideoDetail>
              </div>
              <div className="five wide column">
                <VideoList
                  videos={videos}
                  setSelectedVideo={setSelectedVideo}
                />
              </div>
            </div>
          </div>
        </div>
        <UploadedImagesProvider>
          <Layout className="layout">
            <Header style={{ background: "#fff", padding: "0 300px" }}>
              OCR DEMO
            </Header>
            <Content style={{ padding: "0 300px" }}>
              <Breadcrumb style={{ margin: "16px 0" }}>
                <Breadcrumb.Item>OCR Analyzer</Breadcrumb.Item>
              </Breadcrumb>
              <div style={{ background: "#fff", padding: 24, minHeight: 280 }}>
                <FileUpload />
                <Results />
              </div>
            </Content>
            <Footer style={{ textAlign: "center" }}>
              React OCR Demo Â©2018
            </Footer>
          </Layout>
        </UploadedImagesProvider>
      </FirebaseContext.Provider>
    </>
  );
}

export default App;
